export default {
    name: "City",
    data(){
        return {
            hotList: [],
            cityList: [],
            isLoading: true
        }    
    },
    mounted(){
        //判断是否有本地数据，有就调用
        var hotList = window.localStorage.getItem('hotList');
        var cityList = window.localStorage.getItem('cityList');
        if(hotList && cityList){ 
            this.hotList = JSON.parse(hotList);
            this.cityList = JSON.parse(cityList);
            this.isLoading = false;
        }else{
            //请求数据
            this.axios.get('https://openapi.meituan.com/poi/city').then( (res) => {

                var msg = res.statusText;
                if(msg === 'OK'){
                    var cities = res.data.data;
                    var hotList = this.formatCityList(cities).hotList;
                    var cityList = this.formatCityList(cities).cityList;
                    this.hotList = hotList;
                    this.cityList = cityList;
                    this.isLoading = false;
                    //本地储存数据
                    window.localStorage.setItem('hotList', JSON.stringify(hotList));
                    window.localStorage.setItem('cityList', JSON.stringify(cityList));

                }
            })
        }
    },
    methods: {
        // 分类热门城市列表和分类首字母城市列表
        formatCityList(cities){
            var cityList = [];  //[{ index: "A", list: [{ name: 安庆, id: 123}] } ]
            var hotList = [];

            // 完善热门城市列表
            for(var i=0;i<cities.length;i++){
                if(i <= 14){
                    hotList.push(cities[i]);
                }
            }

            //完善首字母分类城市列表
            for (var i=0;i<cities.length;i++){
                var firstLetter = cities[i].pinyin.substring(0,1).toUpperCase();
                if(toCom(firstLetter)){ //添加新的某个字母开头的列表
                    cityList.push( { index: firstLetter, list: [cities[i]] });
                }else{ //把城市放到对应的首字母开头的列表
                    for(var j=0;j<cityList.length;j++){
                        if(cityList[j].index === firstLetter){
                            cityList[j].list.push( cities[i] );
                        }
                    }

                }
            }
            //判断是否已经存在某个字母开头的列表，没有就新建
            function toCom(firstLetter){
                for(var i=0;i<cityList.length;i++){
                    if(cityList[i].index === firstLetter){
                        return false;
                    }
                }
                return true;
            }
            //对城市列表按首字母进行排序
            cityList.sort((n1,n2)=>{
                if(n1.index > n2.index){
                    return 1;
                }else if(n1.index < n2.index){
                    return -1;
                }else{
                    return 0;
                }
            })

            return{
                hotList,
                cityList
            }
        },
        // 点击首字母索引栏滚动到对应的位置
        handleToIndex(index){
            var h2 = this.$refs.city_sort.getElementsByTagName('h2');
            this.$refs.city_list.handleScrollTo(-h2[index].offsetTop);
        },
        // 修改城市状态管理，并储存到本地
        handleToCity(name, id){
            this.$store.commit('city/CITY_INFO', { name, id });
            window.localStorage.setItem('nowNm', name);
            window.localStorage.setItem('nowId', id);
            this.$router.push('/movie/nowPlaying');
        }


    }
}
